import React, { Component } from "react";
import Timeline from "./Timeline";
import CreateChirp from "./CreatePostForm";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      chirps: [],
      createChirp: false
    };
    this.handleCreateChirp = this.handleCreateChirp.bind(this);
    this.handleSubmitChirp = this.handleSubmitChirp.bind(this);
  }

  handleCreateChirp() {
    this.setState({ createChirp: true });
  }

  handleSubmitChirp() {
    this.setState({ createChirp: false });
  }

  render() {
    if (this.state.createChirp === false) {
      return (
        <div className="container">
          <timeLine />
          <button onClick={this.handleCreateChirp}>Create Chirp</button>
        </div>
      );
    } else {
      return <CreatePostForm handleSubmitChirp={this.handleSubmitChirp} />;
    }
  }
}

export default App;
