import React, { Component } from "react";

class CreateChirp extends Component {
  constructor(props) {
    super(props);

    this.state = {};
    this.handleInput = this.handleInput.bind(this);
  }

  handleInput(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit() {
    props.handleSubmittedChirp(this.state);
  }

  render() {
    return (
      <div className="container">
        <label>Input Chirp Content</label>
        <input type="text" name="chirp-content" onChange={this.handleInput} />
        <button onClick={this.handleSubmit}>Submit Chirp</button>
      </div>
    );
  }
}

export default CreateChirp;
