import React, { Component } from 'react';
import Timeline from './Timeline';
import CreatePostForm from './CreatePostForm';



class App extends Component {
    constructor(props) {
        super(props)

    this.state = {
        posts: [],
        createPost: false
    }

    this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleSubmit(newPost) {
        this.setState({
            posts: [...this.state.posts, newPost],
            createPost: false
        })
        console.log(this.state);
    }

    handleNewChirpButtonClick() {
        this.setState({
            createPost: true
            }  
        )
    }

    render() {


        if (this.state.createPost === false) {
        return (
            <div>
                <Timeline posts = { this.state.posts } />
                <button onClick = { handleNewChirpButtonClick } >Create Chirp</button>
            </div>
        )
        } else {
            return (
            <CreatePostForm  handleSubmit = { this.handleSubmit } />
            )
        }
    }
}

export default App;
