import React, { Component } from "react";
import Timeline from "./Timeline";
import CreateChirp from "./CreatePostForm";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      chirps: [],
      createChirp: false,
      numChirps: 0
    };
    this.handleCreateChirp = this.handleCreateChirp.bind(this);
    this.handleSubmittedChirp = this.handleSubmittedChirp.bind(this);
  }

  handleCreateChirp() {
    this.setState({ createChirp: true });
  }

  handleSubmittedChirp(chirp) {
    this.setState({
      chirps: [chirp, ...this.state.chirps],
      createChirp: false
    });
  }

  render() {
    if (this.state.createChirp === false) {
      return (
        <div className="container">
          <Timeline />
          <button onClick={this.handleCreateChirp}>Create Chirp</button>
        </div>
      );
    } else {
      return <CreateChirp handleSubmittedChirp={this.handleSubmittedChirp} />;
    }
  }
}

export default App;
