import React, { Component } from "react";

class CreateChirp extends Component {
  constructor(props) {
    super(props);

    this.state = {};
  }

  render() {
    return (
      <div className="container">
        <label>Input Chirp Content</label>
        <input type="text" name="chirp-content" />
        <button onClick={this.props.handleSubmitChirp}>Submit Chirp</button>
      </div>
    );
  }
}

export default CreateChirp;
