import React, { Component } from 'react';


class CreatePostForm extends Component {
    constructor(props) {
        super(props)

        this.state = {
            name: "",
            title: "",
            content: "",
        }

        this.handleInputChange = this.handleInputChange.bind(this);

    }

    handleInputChange(e) {
        console.log(e.target.value);
    }

    render() {

        return (
        <div>
            <label>Name</label>
            <input name="name" onChange= { this.handleInputChange }/>
            <label>Title</label>
            <input name="Title" onChange= { this.handleInputChange }/>
            <label>Content</label>
            <input name="Content" onChange= { this.handleInputChange }/>
        </div>
        )
    }


}


export default CreatePostForm;
